import { Injectable } from '@angular/core';
import { Subject } from 'rxjs';
import {io} from 'socket.io-client';
import { Message } from '../models/message.dto';
@Injectable({
  providedIn: 'root'
})
export class ChatService {

  socket = io(import.meta.env.NG_APP_API_URL);
  messageStream: Subject<Message> = new Subject<Message>();
  constructor() { 
    this.socket.on('messageRec', (crypto) => {
      const message = this.decryptMessage(crypto);
      console.log(message);
      this.messageStream.next(message);
    });
  }

  connect(){
    this.socket.connect();
  }
  disconnect(){
    this.socket.disconnect();
  }
  sendMessage(message: string){
    const crypto = this.encryptMessage(message)
    this.socket.emit('message', crypto);
    this.messageStream.next({text:message, crypto:crypto});
  }
  encryptMessage(message: string): string{
    //todo
    return `crypto ${message}`;
  }
  decryptMessage(crypto: string): Message {
    //todo
    return {text:'decrypt', crypto:'a'};
  }
}
