import { Injectable } from '@angular/core';
import { Subject, zip } from 'rxjs';
import {io} from 'socket.io-client';
import { Message } from '../models/message.dto';
import { Store } from '@ngrx/store';
import { User } from '../models/user.model';
import { UserState } from '../store/user.state';
import { selectEncryptionType, selectId, selectUsername } from '../store/user.selector';
import { RC4 } from '../encryption/RC4';
import { XTEA } from '../encryption/XTEA';
import { EncryptionType } from '../models/algorithms';
import { TigerHash } from '../hash/TigerHash';

@Injectable({
  providedIn: 'root'
})
export class ChatService {
  rc4 = new RC4();
  xtea = new XTEA();
  tigerHash = new TigerHash();
  userinfo$ = zip(this.store.select(selectUsername), this.store.select(selectId));
  socket = io(import.meta.env.NG_APP_API_URL);
  messageStream: Subject<Message> = new Subject<Message>();
  constructor(private store: Store<UserState>) { 
    this.userinfo$.subscribe(([username, id]) => {
      this.username = username;
      this.id = id;
    });
    this.socket.on('messageRec', (crypto) => {
      let message = null;
      if (crypto.file)
        message = this.receiveFile(crypto);
      else
        message = this.decryptMessage(crypto);
      this.messageStream.next(message!);
    });
  }
  username: string | null = null;
  id: string | null = null;


  connect(){
    this.socket.connect();
  }
  disconnect(){
    this.socket.disconnect();
  }
  sendMessage(message: string, encryptionType: EncryptionType){
    console.log(this.username);
    console.log(this.id);
    message = message.trim();
    const crypto = this.encryptMessage(message, encryptionType)
    const messageObject : Message = {algorithm: encryptionType, text:message, crypto:crypto, owner: true, senderName: this.username!, senderId: this.id!, file: false} 
    this.messageStream.next({...messageObject});
    messageObject.text = null;
    this.socket.emit('message', messageObject);
  }
  sendFile(file: File, encryptionType: EncryptionType){
    const reader = new FileReader();
    reader.readAsText(file);
    reader.onload = () => {
      let crypto = this.encryptMessage(reader.result as string, encryptionType);
      crypto += this.tigerHash.hash(Uint8Array.from(crypto.split(``).map((char) => char.charCodeAt(0))));

      const messageObject : Message = {algorithm: encryptionType, text:null, crypto:crypto, owner: true, senderName: this.username!, senderId: this.id!, file: true, fileContent: null} 
      this.messageStream.next({...messageObject});
      messageObject.text = null;
      this.socket.emit('message', messageObject);
    }
  }

  receiveFile(message: Message){
    const crypto = message.text?.slice(0, message.text.length - 24);
    const hash = message.text?.slice(message.text.length - 24);
    const newHash = this.tigerHash.hash(Uint8Array.from(crypto!.split(``).map((char) => char.charCodeAt(0))));
    if (hash !== newHash)
      console.log(`Hashes don't match`);
    const decryptedMessage = this.decryptMessage({...message, crypto: crypto!});
    const fileString = decryptedMessage.text;
    const file = new File([fileString!], `file.txt`, {type: 'text/plain'});
    const msg: Message = {algorithm: message.algorithm, text:'', crypto:message.crypto, owner: message.senderId === this.id, senderName: message.senderName, senderId: message.senderId, file: true};
  }
  encryptMessage(message: string, encryptionType: EncryptionType): string{
    if (encryptionType === EncryptionType.None)
      return message;
    else if (encryptionType === EncryptionType.RC4)
      return this.rc4.Encrypt(message);
    else if (encryptionType === EncryptionType.XTEA)
      return this.xtea.Encrypt(message);
    else
      return message;
  }
  decryptMessage(cryptoMsg: Message): Message {
    let text = "";
    if (cryptoMsg.algorithm === EncryptionType.None)
      return cryptoMsg;
    else if (cryptoMsg.algorithm === EncryptionType.RC4)
      text = this.rc4.Decrypt(cryptoMsg.crypto);
    else if (cryptoMsg.algorithm === EncryptionType.XTEA)
      text = this.xtea.Decrypt(cryptoMsg.crypto);
    const msg: Message = {algorithm: cryptoMsg.algorithm, text:text, crypto:cryptoMsg.crypto, owner: cryptoMsg.senderId === this.id, senderName: cryptoMsg.senderName, senderId: cryptoMsg.senderId, file: false};
    return msg;
  }
}
