import { Component } from '@angular/core';
import { Store } from '@ngrx/store';
import { User } from '../models/user.model';
import { UserState } from '../store/user.state';
import { AddUsername } from '../store/user.actions';
import { Router } from '@angular/router';

@Component({
  selector: 'app-landing-page',
  templateUrl: './landing-page.component.html',
  styleUrls: ['./landing-page.component.scss']
})
export class LandingPageComponent {
  constructor(private store: Store<UserState>, private router: Router) {
    console.log(import.meta.env.NG_APP_SECRET);
    console.log(import.meta.env.NG_APP_API_URL);
  }

  username: string = '';

  redirect() {
    this.store.dispatch(AddUsername({ username: this.username }));
    this.router.navigate(['/chat']);
  }
}
<div class="h-full bg-gray-700" >
    <div class="grid grid-cols-3 h-full justify-center items-center">
        <div></div>
        <div class="flex flex-col justify-center items-center">
            <p class="text-gray-300 text-4xl text-center ">Welcome to E2E chat</p>
            <p class="text-gray-500 text-xl text-center">Enter a username</p>
            <input [(ngModel)]="username" name="username-input" placeholder="Username" id="usernameId" class="mx-auto">
            <div class="flex justify-center items-center">
                <button disabled="if username==''" class="bg-green-700 hover:bg-green-600 text-slate-300 font-bold py-2 px-4 rounded-full mt-4 disabled:bg-green-700/25 disabled:text-slate-300/25" (click)="redirect()">Chat</button>
            </div>
        </div>
        <div></div>
    </div>
</div>
<router-outlet></router-outlet>
