export class RC4 {
    private S: number[] = []

    public encrypt(message: string){
        if (this.S.length === 0) {
            this.S = this.KSA(import.meta.env.NG_APP_SECRET);
        }
        const coded = this.PRGA(message);
        return coded;
    }
    private KSA(key: string) {
        let S = [];
        for (let i = 0; i < 256; i++) {
            S[i] = i;
        }
        let j = 0;
        for (let i = 0; i < 256; i++) {
            j = (j + S[i] + key.charCodeAt(i % key.length)) % 256;
            let tmp: number = S[i];
            S[i] = S[j];
            S[j] = tmp;
        }
        return S;
    }
    private PRGA(message: string) {
        let i = 0;
        let j = 0;
        if (this.S.length === 0) {
            this.S = this.KSA(import.meta.env.NG_APP_SECRET);
        }
        let cipher = [];
        for (let n = 0; n < message.length; n++) {
            i = (i + 1) % 256;
            j = (j + this.S[i]) % 256;
            let tmp: number = this.S[i];
            this.S[i] = this.S[j];
            this.S[j] = tmp;
            let k = this.S[(this.S[i] + this.S[j]) % 256];
            cipher.push(message.charCodeAt(n) ^ k);
        }
        let coded = cipher.join('');
        return coded;
    }   
}
